$(window).scroll(function () {
	$(".faq-answer").each(function () {
		0 != $(this).height() && ($(this).stop().animate({
				paddingTop : 0,
				height : 0,
				paddingBottom : 0
			}, 500), setTimeout(function () {
				$(this).css("box-shadow", "none")
			}, 500))
	})
});
